import React from "react";
import moment from "moment";
import { makeStyles, Typography, Hidden, Container } from "@material-ui/core";
import CompetitionNamePanel from "../CompetitionNamePanel";

const useStyles = makeStyles((theme) => ({
  gamePanel: {
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    marginTop: 16,
  },
  cardHeader: {
    width: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: 0,
  },
  gameNameSm: {
    textAlign: "center",
    marginTop: 16,
  },
  teamShield: {
    height: 16,
    width: 16,
    padding: 4,
    "& img": {
      height: "100%",
      width: "100%",
    },
  },
}));

const GamePanel = ({ competition, game: { teamA, teamB, datetime } }) => {
  const classes = useStyles();
  return (
    <>
      <Hidden mdDown>
        <Container className={classes.cardHeader}>
          <CompetitionNamePanel competition={competition} />
          <Typography variant="h6" color="dark" component="p">
            <span className={classes.teamShield}>
              <img src={teamA.shield} alt="team shield" />
            </span>
            {teamA.name.toUpperCase()}-
            <span className={classes.teamShield}>
              <img src={teamA.shield} alt="team shield" />
            </span>
            {teamB.name.toUpperCase()}
          </Typography>
          <Typography variant="subtitle1" color="dark" component="p">
            {moment(datetime).format("ddd MM YYYY HH:SS")}
          </Typography>
        </Container>
      </Hidden>
      <Hidden mdUp>
        <Container className={classes.cardHeader}>
          <CompetitionNamePanel competition={competition} />
          <Typography variant="subtitle1" color="dark" component="p">
            {moment(datetime).format("ddd MM YYYY HH:SS")}
          </Typography>
        </Container>
        <Typography
          variant="h6"
          color="dark"
          component="p"
          className={classes.gameNameSm}
        >
          {teamA.name.toUpperCase()}-{teamB.name.toUpperCase()}
        </Typography>
      </Hidden>
    </>
  );
};

export default GamePanel;
