import React, { useState, useEffect, useContext } from "react";
import ReactDOM from "react-dom";
import Context from "../../context/index";
import moment from "moment";
import { server_root_path } from "../../config/index.json";
import { useComponentState } from "../../hooks";
import { makeStyles } from "@material-ui/core/styles";
import Card from "@material-ui/core/Card";
import {
  CardContent,
  CardMedia,
  Collapse,
  Typography,
  Chip,
} from "@material-ui/core";
import capitalize from 'capitalize';
import { DataGrid } from "@material-ui/data-grid";
import Modal from "../Modal/index";
import BetForm from "../BetForm/index";
import CardActionBar from "../CardActionBar";
import AccessTimeRoundedIcon from "@material-ui/icons/AccessTimeRounded";

const useStyles = makeStyles((theme) => ({
  root: {
    textAlign: "left",
    padding: 16,
    justifyContent: "space-between",
  },
  media: {
    height: 0,
    paddingTop: "56.25%", // 16:9
  },
  expand: {
    transform: "rotate(0deg)",
    marginLeft: "auto",
    transition: theme.transitions.create("transform", {
      duration: theme.transitions.duration.shortest,
    }),
  },
  expandOpen: {
    transform: "rotate(180deg)",
  },
  betStat: {
    height: 275,
    width: "100%",
  },
  collapse: {
    paddingTop: 16,
  },
  cardHeader:{
    paddingTop: 16,
    paddingBottom: 16
  },
  badge: {
    marginRight: 8,
    marginBottom: 8,
    padding:8
  }
}));

const GameCardDashBoard = ({
  competition,
  bettingContract,
  accounts,
  game: {
    cover,
    description,
    ended,
    started,
    team1Name,
    team1Score,
    team2Name,
    team2Score,
    winner,
    start,
    id,
  },
}) => {
  const classes = useStyles();
  // Access custom hooks to display errors and loading animations
  const {
    state,
    setState,
    Error,
    LoadingAnimation,
    isModalToogled,
    setModalToogled,
  } = useComponentState();
  // 0- check if user favorite the current game
  const { favorites, setFavorites } = useContext(Context);
  const [isFavorite, setIsFavorite] = useState();

  useEffect(() => {
    const favoriteId = favorites.find((id) => id === id);
    favoriteId ? setIsFavorite(true) : setIsFavorite(false);
  }, []);

  const handleAddFavorite = (id) => {
    if (!isFavorite) {
      setIsFavorite(true);
      localStorage.setItem("favoriteGames", JSON.stringify([...favorites, id]));
      setFavorites([...favorites, id]);
    } else {
      setIsFavorite(false);
      const newFavorites = favorites.filter((id) => id !== id);
      localStorage.setItem("favoriteGames", JSON.stringify(newFavorites));
      setFavorites(newFavorites);
    }
  };

  const columns = [
    { field: "user", headerName: "#", width: 600 },
    { field: "value", headerName: "value", width: 300 },
    { field: "date", headerName: "date", width: 300 },
  ];

  // 3- Transactions
  const [expanded, setExpanded] = React.useState(false);
  const handleExpandClick = () => {
    setExpanded(!expanded);
    // setTransactions
  };

  if (state.status === "loading") return <LoadingAnimation />;
  else if (state.status === "loaded") {
    return (
      <>
        <Card className={classes.root}>
          <div className={classes.cardHeader}>
            <Chip className={classes.badge} color="primary" icon={<AccessTimeRoundedIcon />} label={moment(parseInt(start + "000")).format(
              "DD/MM/YY HH:SS")}/>
            <Chip className={classes.badge} color="primary" label={ended ? "ended" : started ? "started" : "active"}/>
            <Chip className={classes.badge} color="primary" label={competition?.name}/>
          </div>
          <CardMedia
            image={server_root_path + cover}
            className={classes.media}
            title={`${capitalize(team1Name)} vs ${capitalize(team2Name)}`}
          />
          <CardContent>
            <Typography variant="h5" component="h5">
              {`${capitalize(team1Name)} vs ${capitalize(team2Name)}`}
            </Typography>
            <Typography variant="body1" component="p">
              {description}
            </Typography>
          </CardContent>
          <CardActionBar
            handleAddFavorite={handleAddFavorite}
            isFavorite={isFavorite}
            handleExpandClick={handleExpandClick}
            expanded={expanded}
            gameId={id}
            setModalToogled={setModalToogled}
          />
          <Collapse
            className={classes.collapse}
            in={expanded}
            timeout="auto"
            unmountOnExit
          >
            <div className={classes.datagrid}>

            </div>
          </Collapse>
        </Card>
        {isModalToogled &&
          ReactDOM.createPortal(
            <Modal
              component={BetForm}
              title={`Place a bet on :`}
              buttonLabel="confirm"
              setModalToogled={setModalToogled}
              game={{
                cover,
                description,
                ended,
                started,
                team1Name,
                team1Score,
                team2Name,
                team2Score,
                winner,
                id,
              }}
              bettingContract={bettingContract}
              accounts={accounts}
            />,
            document.querySelector("body")
          )}
      </>
    );
  } else {
    return <Error code={500} />;
  }
};

export default GameCardDashBoard;
