import React, { useState, useEffect } from "react";
import ModalClosePanel from "../ModalClosePanel/index";
import useMediaQuery from "@material-ui/core/useMediaQuery";
import clsx from "clsx";
import {
  Grid,
  makeStyles,
  TextField,
  Typography,
  Button,
  Hidden,
  MenuItem,
  Select,
} from "@material-ui/core";

const useStyles = makeStyles(() => ({
  root: {},
  containerFlex: {
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
  },
  textfield: {
    marginTop: "1rem",
    marginBottom: "1rem",
    width: "100%",
  },
  button: {
    marginTop: "1rem",
    width: "100%",
  },
  formLg: {
    borderRadius: "4px",
  },
  form: {
    backgroundColor: "#FFF",
    padding: 24,
    minHeight: "100%",
  },
  formContainer: {
    width: "100%",
  },
  formContainerSm: {
    minHeight: "100%",
    top: "4rem",
    position: "relative",
  },
  title: {
    marginBottom: "1rem",
  },
  backPanel: {
    display: "flex",
    alignItems: "center",
  },
  backArrow: {
    marginBottom: 24,
    marginRight: 8,
    cursor: "pointer",
  },
}));

const BetForm = ({
  title,
  buttonLabel,
  setModalToogled,
  game: {
    cover,
    description,
    ended,
    started,
    team1Name,
    team1Score,
    team2Name,
    team2Score,
    winner,
    id,
  },
}) => {
  const matches = useMediaQuery("(max-width:600px)");
  const classes = useStyles();
  const [isFormValid, setFormValid] = useState(false);
  const [formData, setFormData] = useState({
    betSide: {
      value: "",
      helperText: "",
      error: false,
    },
    betValue: {
      value: "",
      error: false,
    },
  });
  const validateFields = () => {
    let formIsValid = true;
    fields.map((field) => {
      if (field.required && formData[field.id] === "") {
        setFormData({
          ...formData,
          [formData[field.id]]: {
            ...formData[field.id],
            error: true,
            helperText: "field is required",
          },
        });
        formIsValid = false;
      }
    });
    setFormValid(formIsValid);
  };

  useEffect(()=>{

    console.log(formData)

  }, [formData])
  const fields = [
    {
      type: "select",
      labelId: "Choose your side",
      id: "betSide",
      value: "",
      items: [
        { id: "1", name: team1Name?.toUpperCase() },
        { id: "N", name: "NULL" },
        { id: "2", name: team2Name?.toUpperCase() },
      ],
      onChange: (event) => {
        const newFormData = {
          ...formData,
          betSide: { ...formData.betSide, value: event.target.value },
        };
        setFormData(newFormData);
        validateFields();
      },
    },
    {
      type: "number",
      error: "",
      required: true,
      readOnly: false,
      helperText: "",
      label: "Bet amount",
      id: "betValue",
      onInput: (event) => {
        const newFormData = {
          ...formData,
          betValue: { ...formData.betValue, value: event.target.value },
        };
        setFormData(newFormData);
        validateFields();
      },
    },
  ];

  const handleSubmit = () => {};

  return (
    <Grid container className={classes.root}>
      <Hidden mdDown>
        <Grid item lg={4}></Grid>
      </Hidden>
      <Grid item xs={12} lg={4} className={classes.containerFlex}>
        <div
          className={
            matches
              ? clsx(classes.formContainer, classes.formContainerSm)
              : classes.formContainer
          }
        >
          <form
            noValidate
            autoComplete="off"
            className={
              matches ? classes.form : clsx(classes.form, classes.formLg)
            }
          >
            <ModalClosePanel setModalToogled={setModalToogled} />
            <Typography variant="h4" component="h1" className={classes.title}>
              {title}
            </Typography>
            {fields.map((field) =>
              field.type === "number" ? (
                <TextField
                  key={field.id}
                  id={field.id}
                  label={field.label.toUpperCase()}
                  variant="outlined"
                  className={classes.textfield}
                  required={field.required}
                  readOnly={field.readOnly}
                  error={formData[field.id].error}
                  helperText={formData[field.id].helperText}
                  type={field.type}
                  value={formData[field.id].value}
                  onInput={field.onInput}
                />
              ) : (
                field.type === "select" && (
                  <Select
                    labelId={field.labelId}
                    id={field.id}
                    value={formData[field.id].value}
                    onChange={field.onChange}
                    className={classes.textfield}
                    error={formData[field.id].error}
                  >
                    {field.items?.map((item) => (
                      <MenuItem key={`game-${item.id}`} value={item.id}>
                        {item.name}
                      </MenuItem>
                    ))}
                  </Select>
                )
              )
            )}
            <Button
              color="primary"
              variant="contained"
              className={classes.button}
              size="large"
              onClick={handleSubmit}
              disabled={!isFormValid}
            >
              {buttonLabel}
            </Button>
          </form>
        </div>
      </Grid>

      <Hidden mdDown>
        <Grid item lg={4}></Grid>
      </Hidden>
    </Grid>
  );
};

export default BetForm;
