import React, { useEffect, useState } from "react";
import Web3 from "web3";
import detectEthereumProvider from "@metamask/detect-provider";
import LoadingAnimation from "../../components/LoadingAnimation/index";
import Error from "../../components/Error/index";

const ComponentState = ({ component: Component, ...props }) => {
  const [provider, setProvider] = useState();
  const [state, setState] = useState({
    status: "loaded",
    code: null,
  });

  useEffect(() => {
    const getAndSetProvider = async () => {
      if (process.env == "developement") {
        setProvider(new Web3("http://localhost:8545"));
      } else {
        const provider = await detectEthereumProvider();
        if (provider) {
          setProvider(provider);
        } else {
          setState({ status: "error", code: 499});
        }
      }
    };

    getAndSetProvider();
  }, []);

  if (state.status === "loading") {
    return <LoadingAnimation />;
  } else if (state.status == "error") {
    return <Error code={state.code} />;
  } else {
    return <Component setState={setState} {...props} />;
  }

  return <Component setState={setState} {...props} />;
};

export default ComponentState;
