import React from "react";
import moment from "moment";
import { Container, makeStyles, Typography, Hidden } from "@material-ui/core";
import CardContent from "@material-ui/core/CardContent";
import BetButtonBar from "../../BetButtonBar";
import GamePanel from "../../GamePanel/index";

const useStyles = makeStyles((theme) => ({
  cardHeader: {
    width: "100%",
    display: "flex",
    alignItems: "center",
    justifyContent: "space-between",
    padding: 0,
  },
  gameNameSm: {
    textAlign: "center",
    marginTop: 16,
  },
}));

const CardHeader = ({ teamA, teamB, datetime, draw, competition }) => {
  const classes = useStyles();
  return (
    <CardContent>
      <GamePanel
        competition={competition}
        teamA={teamA}
        teamB={teamB}
        datetime={datetime}
      />
      <BetButtonBar gameStatus teamA={teamA} teamB={teamB} draw={draw} />
    </CardContent>
  );
};

export default CardHeader;
